    def append_new_rule(self,new_rule):
        with open('firewall.sh', 'a') as rsh:
            rsh.write("\n"+new_rule)
            #self.label_add_rule_msg.setText("Rule Added to the Firewall. ")
        try:
            subprocess.call(['bash','firewall.sh'])
            return True
        except ValueError:
            return False

