    def fetch_rules(self):
        rule = []
        with open('firewall.sh', 'r') as rsh:
            num = 1
            while (rsh):
                line = rsh.readline()
                if line == "\n":
                    continue
                if line == "":
                    break
                line = line.split()
                table = ""
                chain = ""
                port = ""
                website = ""
                ip_add = ""
                traffic = ""
                action = ""
                for e in line:

                    if e == '-t':
                        table = line[line.index(e) + 1]
                        # print(table)
                    if e == '-A':
                        chain = line[line.index(e) + 1]
                        # print(chain)
                    if e == '-p':
                        traffic = line[line.index(e) + 1]
                        # print(traffic)
                    if e == '--dport':
                        port = line[line.index(e) + 1]
                        # print(port)
                    if e == '-s':
                        ip_add = line[line.index(e) + 1]
                        # print(ip_add)
                    if e == '-j':
                        action = line[line.index(e) + 1]
                    if e == '-d':
                        website = line[line.index(e) + 1]
                        # print(action)
                if table == "" and action != "":
                    table = "filter"
                if action != "":
                    data1 = [table, chain, traffic, port,ip_add,website, action]
                    #self.llist.insert(data1)
                    rule.append([str(num-2),table, chain, traffic, port,ip_add,website, action])
                num+=1
        return rule

